parameters.jsCode:
// Process all generated content
const originalPrompt = $node['ZEROCODE Webhook'].json.user_prompt;
const expertContent = $node['Generate Expert Content'].json.choices[0].message.content;
const hookSummary = $node['Create Hook Summary'].json.choices[0].message.content;
const twitterThread = $node['Create Twitter Thread'].json.choices[0].message.content;
const tiktokScript = $node['Create TikTok Script'].json.choices[0].message.content;
const emailContent = $node['Create Email Content'].json.choices[0].message.content;
const videoData = $node['Create AI Video'].json;

// Parse Twitter threads
const tweetParts = twitterThread.split('|').map(part => part.trim());
const threads = [];

for (let part of tweetParts) {
  const threadMatch = part.match(/THREAD \d+: (.+)/);
  if (threadMatch) {
    threads.push(threadMatch[1].trim().substring(0, 270));
  }
}

// Ensure 3 threads
while (threads.length < 3) {
  threads.push('Follow for more business insights! üöÄ');
}

// Parse email
const emailLines = emailContent.split('\n');
const subjectLine = emailLines.find(line => line.toLowerCase().includes('subject')) || hookSummary;
const emailBody = emailContent.replace(/.*subject.*\n/i, '').trim();

// Generate timestamps
const now = new Date();
const sessionId = now.getTime().toString();
const tweetTime = new Date(now.getTime() + 15 * 60 * 1000);
const emailTime = new Date(now.getTime() + 30 * 60 * 1000);

// Determine theme
const theme = originalPrompt.toLowerCase().includes('productivity') ? 'Productivity' :
             originalPrompt.toLowerCase().includes('business') ? 'Business' :
             originalPrompt.toLowerCase().includes('marketing') ? 'Marketing' :
             originalPrompt.toLowerCase().includes('leadership') ? 'Leadership' :
             originalPrompt.toLowerCase().includes('finance') ? 'Finance' :
             'Business Insights';

const mediumTitle = hookSummary + ' - ' + theme + ' for Entrepreneurs';

return {
  sessionId: sessionId,
  timestamp: now.toISOString(),
  date: now.toISOString().split('T')[0],
  originalPrompt: originalPrompt,
  cleanPrompt: originalPrompt.replace('ZEROCODE:', '').trim(),
  expertContent: expertContent,
  hookSummary: hookSummary,
  theme: theme,
  thread1: threads[0],
  thread2: threads[1],
  thread3: threads[2],
  tiktokScript: tiktokScript,
  emailSubject: subjectLine.replace(/.*subject:?\s*/i, '').trim(),
  emailBody: emailBody,
  mediumTitle: mediumTitle,
  imageUrl: 'https://picsum.photos/1200/630',
  videoId: videoData.id || null,
  videoStatus: videoData.status || 'pending',
  tweetScheduleTime: tweetTime.toISOString(),
  emailScheduleTime: emailTime.toISOString(),
  wordCount: expertContent.split(' ').length,
  platforms: ['twitter', 'tiktok', 'blog', 'email'],
  contentType: 'zerocode_session'
};

// Format blog post for publishing
const data = $node['Process All Content'].json;

const slug = data.cleanPrompt
  .toLowerCase()
  .replace(/[^a-z0-9]+/g, '-')
  .replace(/^-+|-+$/g, '')
  .substring(0, 60);

const formattedBody = '<h2>The Challenge</h2>\n<p>' + data.hookSummary + '</p>\n\n<h2>The Solution</h2>\n' + 
  data.expertContent.split('\n\n').map(function(paragraph) {
    return paragraph.trim() ? '<p>' + paragraph.trim() + '</p>' : '';
  }).filter(function(p) { return p; }).join('\n') + 
  '\n\n<h2>Key Takeaways</h2>\n<ul>\n<li>Leverage automation to scale your efforts</li>\n<li>Focus on high-value activities</li>\n<li>Build systems that compound results</li>\n</ul>\n\n<h2>Next Steps</h2>\n<p>Ready to implement? Start with one platform and expand.</p>';

const blogPost = {
  title: data.mediumTitle,
  author: 'Michael Savvick',
  published_date: new Date().toISOString().split('T')[0],
  summary: data.hookSummary,
  meta_description: data.hookSummary.substring(0, 150) + '...',
  body: formattedBody,
  tags: [data.theme, 'ZeroCode', 'Automation', 'Business'],
  canonical_url: 'https://savvick.com/blog/' + slug,
  featured_image_url: data.imageUrl,
  session_id: data.sessionId
};

return { blogPost: blogPost, slug: slug };

// Create proof of work entry
const data = $node['Process All Content'].json;
const content = $node['Generate Expert Content'].json.choices[0].message.content;

const categories = [];
const contentLower = (data.cleanPrompt + ' ' + content).toLowerCase();

if (contentLower.includes('productivity')) categories.push('Productivity');
if (contentLower.includes('marketing')) categories.push('Marketing');
if (contentLower.includes('business')) categories.push('Business');
if (contentLower.includes('automation')) categories.push('Automation');
if (categories.length === 0) categories.push('General Business');

const tags = [data.theme, 'ZeroCode', 'AI-Generated'];

const proofEntry = {
  timestamp: new Date().toISOString(),
  date: new Date().toISOString().split('T')[0],
  session_id: data.sessionId,
  zerocode_input: data.originalPrompt,
  chatgpt_output: content,
  title: data.mediumTitle,
  summary: data.hookSummary,
  categories: categories,
  tags: tags,
  word_count: content.split(' ').length,
  platforms_activated: ['Twitter', 'TikTok', 'Blog', 'Email'],
  content_type: 'ZEROCODE_Generation',
  theme: data.theme,
  status: 'published',
  visibility: 'public'
};

return { proofEntry: proofEntry };

